package array;

public class SimpleSingleDimArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a[]=new int[5];		//declaration
		a[0]=12;						//initialization
		a[1]=13;
		a[2]=14;
		a[3]=15;
		a[4]=16;
		for(int i=0;i<=4;i++)   // For Print Use for loop & store And Compare in it i
			
			System.out.println(a[i]);  // Print Array 
	}

}
