package controlstatement;

public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=5;
		
		if(num%2==0)
		{ 
			System.out.println("The number is Even Number");
		}
		else 
		{
			System.out.println("The number is Odd Number");
		}
		}

	}


