package loopsinjava;

public class NestedForExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//loop of i  
		{
		for(int i=1;i<=3;i++)
		
			//loop of j  
		
			{
			for(int j=1;j<=3;j++) 
		        System.out.println(i+" "+j);  
		}//end of i  
		}//end of j  
		
	}

}
